var endpoint = req.body.endpoint, method = req.body.method || 'GET', headers = req.body.headers || {};

const child = spawnSync('curl', [endpoint, '-H', headers, '-X', method]);

if (child.stdout) {
    res.send(child.stdout)
} else if (child.err) {
    res.send(child.err)
} else {
    res.send(child.stderr)
}
